import{_ as c}from"./chunks/articleMeta.vue_vue_type_script_setup_true_lang.88bb6f08.js";import{_ as t,c as r,b as n,w as i,d as l,e as F,a as d,r as y,o as C}from"./app.76f071c4.js";const b=JSON.parse('{"title":"css 使用 calc() 无效的两种常见情况","description":"","frontmatter":{"title":"css 使用 calc() 无效的两种常见情况","author":"teemwu","date":"2021/01/10 22:46","isPublished":true,"categories":["CSS"],"tags":null,"wordCount":236},"headers":[{"level":1,"title":"css 使用 calc() 无效的两种常见情况","slug":"css-使用-calc-无效的两种常见情况","link":"#css-使用-calc-无效的两种常见情况","children":[{"level":3,"title":"第一种","slug":"第一种","link":"#第一种","children":[]},{"level":3,"title":"第二种","slug":"第二种","link":"#第二种","children":[]},{"level":3,"title":"参考","slug":"参考","link":"#参考","children":[]}]}],"relativePath":"随笔/2021/css 使用 calc() 无效的两种常见情况.md","lastUpdated":1670836236000}'),D={name:"随笔/2021/css 使用 calc() 无效的两种常见情况.md"},A=l("h1",{id:"css-使用-calc-无效的两种常见情况",tabindex:"-1"},[F("css 使用 calc() 无效的两种常见情况 "),l("a",{class:"header-anchor",href:"#css-使用-calc-无效的两种常见情况","aria-hidden":"true"},"#")],-1),_=d(`<h3 id="第一种" tabindex="-1">第一种 <a class="header-anchor" href="#第一种" aria-hidden="true">#</a></h3><p>运算符间没加空格，如</p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;">/*错误示例*/</span></span>
<span class="line"><span style="color:#FFCB6B;">div</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#B2CCD6;">width</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">calc</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">100%-10px</span><span style="color:#89DDFF;">);</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#676E95;">/*正确写法*/</span></span>
<span class="line"><span style="color:#FFCB6B;">div</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#B2CCD6;">width</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">calc</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">100%</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">10px</span><span style="color:#89DDFF;">);</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>为什么要加空格？其实不是所有运算符间都需要加空格，只有 <code>+</code> 和 <code>-</code> 需要加空格，因为运算允许负数的出现，如</p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki"><code><span class="line"><span style="color:#FFCB6B;">div</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#B2CCD6;">width</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">calc</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">100%</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">+</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">-10px</span><span style="color:#89DDFF;">);</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>所以，为了统一，<code>/</code> 和 <code>*</code> 最好都带上空格。</p><h3 id="第二种" tabindex="-1">第二种 <a class="header-anchor" href="#第二种" aria-hidden="true">#</a></h3><p>运算值不带单位，如</p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;">/*错误写法*/</span></span>
<span class="line"><span style="color:#FFCB6B;">div</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#B2CCD6;">width</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">calc</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">+</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">10px</span><span style="color:#89DDFF;">);</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#676E95;">/*正确写法*/</span></span>
<span class="line"><span style="color:#FFCB6B;">div</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#B2CCD6;">width</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">calc</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">0px</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">+</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">10px</span><span style="color:#89DDFF;">);</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>看到这里，你可能会疑问，为啥 0 还要带个单位呢？其实，calc() 函数传入的是一个数学表达式，而表达式的值可以有多种类型，如长度、百分比、角度等， 那如果你传个 0 进去，没单位的话，怎么知道这个 0 是属于什么类型呢？</p><h3 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-hidden="true">#</a></h3><ul><li><a href="https://drafts.csswg.org/css-values-3/#calc-type-checking" target="_blank" rel="noreferrer">https://drafts.csswg.org/css-values-3/#calc-type-checking</a></li></ul>`,12);function h(s,a,u,g,f,m){const p=c,e=y("ClientOnly");return C(),r("div",null,[A,n(e,null,{default:i(()=>[n(p,{modelValue:s.$frontmatter,"onUpdate:modelValue":a[0]||(a[0]=o=>s.$frontmatter=o)},null,8,["modelValue"])]),_:1}),_])}const x=t(D,[["render",h]]);export{b as __pageData,x as default};
